CREATE OR REPLACE PROCEDURE david.pcd_act_acad_plan_num_hrs (p_id_plan integer) AS $body$
DECLARE

  v_num_cursos david.acad_plan.numero_cursos%TYPE;

BEGIN
 
    -- se cuenta el número de cursos que tiene el plan
    select count(id_plan) into STRICT v_num_cursos
    from david.acad_plan_curso
    where id_plan=p_id_plan;

    -- actualiza acad_plan con el número de cursos del plan
    update david.acad_plan set numero_cursos = v_num_cursos
    where id_plan=p_id_plan;

 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
