CREATE OR REPLACE PROCEDURE david.pcd_create_alumno_admision_n ( V_ID_TIPO_DOCUMENTO integer, V_NUM_DOCUMENTO text, V_NOMBRE text, V_PATERNO text, V_MATERNO text, V_ESTADO_CIVIL text, V_NACIONALIDAD text, V_UBIGEO integer, V_COD_RELIGION text, V_SEXO text, V_FECHA_NACIMIENTO timestamp(0), V_CELULAR text, V_CORREO text, V_ID_PLAN_PROGRAMA integer, V_ID_SEDE integer, V_ID_TIPO_BECA_ESTATAL integer, V_CONVOCATORIA text, V_OPCION integer, out CURSOR REFCURSOR ) AS $body$
DECLARE


    p_id_persona integer;
    p_res_id_persona integer;
    p_res_persona_documento integer;
    p_res_persona_doc_condicion integer;
    p_res_persona_natural integer;
    p_res_persona_alumno integer;
    p_res_codigo integer;
    p_res_user integer;
    p_id_nivel_ensenanza integer;
    p_codigo_universitario varchar(20);
    p_res varchar(1);
    l_contar bigint:=0;
    contar_entidad bigint:=0;
    contar_depto bigint:=0;
    l_code varchar(200);
    l_error varchar(200);


BEGIN

    p_id_persona:=0;

    select coalesce((select id_persona from moises.persona_documento where Num_Documento=v_num_documento),0) into STRICT p_id_persona;

    if (p_id_persona=0) then

        select coalesce((select id_persona from moises.persona where trim(both upper(sintilde(nombre||' '||paterno||' '||materno)))=trim(both upper(sintilde(v_nombre||' '||v_paterno||' '||v_materno))) ),0) into STRICT p_id_persona;

        if (p_id_persona=0) then
            p_res_id_persona:=0;
        else
            p_res_id_persona:=1;
        end if;

    else

        p_res_id_persona:=1;

    end if;

    if (p_res_id_persona=0) then

        insert into moises.persona(id_persona,Nombre,paterno,materno)
        VALUES((select max(id_persona)+1 from moises.persona),trim(both v_nombre),trim(both v_paterno),trim(both v_materno))
        returning id_persona into STRICT p_id_persona;

        insert into moises.persona_documento(id_persona,id_tipodocumento,num_documento,es_activo)
        VALUES (p_id_persona,v_id_tipo_documento,v_num_documento,1);

        insert into moises.persona_natural(id_persona,id_tipotratamiento,id_tipoestadocivil,id_tipopais,id_ubigeo,id_tiporeligion,id_tipodocumento,num_documento,sexo,fec_nacimiento,celular,correo,id_nacionalidad)
        VALUES(p_id_persona,1,
               (SELECT id_tipoestadocivil from moises.tipo_estado_civil where nombre_corto=v_estado_civil),
               (select id_tipopais from moises.tipo_pais where iso_a2=v_nacionalidad),
               (select id_ubigeo from moises.ubigueo where depto=substr(v_ubigeo,0,2) and pvcia=substr(v_ubigeo,3,2) and ditto=substr(v_ubigeo,-2) ),
               (select id_tiporeligion from moises.tipo_religion where codigo=v_cod_religion),
               v_id_tipo_documento,v_num_documento,
               (select sexo from moises.tipo_sexo where codigo=v_sexo),
               v_fecha_nacimiento,v_celular,v_correo,
               (select id_tipopais from moises.tipo_pais where iso_a2=v_nacionalidad));

        select ft_max_codigo_conv(1,V_CONVOCATORIA) into STRICT p_codigo_universitario;

        insert into moises.persona_natural_alumno(id_persona,codigo)
        VALUES (p_id_persona,p_codigo_universitario);

        insert into acad_alumno_plan(id_persona,id_plan_programa,id_sede,estado,id_usuario_reg,fecha_registro)
        VALUES (p_id_persona,v_id_plan_programa,v_id_sede,1,5,clock_timestamp());

        insert into eliseo.users(ID,EMAIL,CONTRASENHA,NAME,ID_PERFIL,FECHA_CREATE)
        VALUES (p_id_persona,ft_newformat_users(p_id_persona),'3ca413e61535cf2017496b87412aa332072053f8',v_nombre||' '||v_paterno||' '||v_materno,2,clock_timestamp());

        insert into eliseo.conta_entidad_usuario(ID_ENTIDAD,ID_PERSONA,ESTADO)
        VALUES (7124,p_id_persona,1);

        insert into eliseo.LAMB_USERS_DEPTO(ID,ID_ENTIDAD,ID_DEPTO,ESTADO,ACTIVO)
        VALUES (p_id_persona,7124,(SELECT CASE WHEN id_sede=2 THEN 5 WHEN id_sede=3 THEN 6  ELSE 1 END  from Vw_Alumno_Plan_Programa where id_persona=p_id_persona),1,1);

        insert into eliseo.LAMB_USUARIO_ROL(ID_ROL,ID_PERSONA,ID_ENTIDAD)
        VALUES (44,p_id_persona,7124);

        p_res:='S';

    else

        select coalesce((select Distinct 1 from moises.persona_documento where id_persona=p_id_persona),0) into STRICT p_res_persona_documento;

        if (p_res_persona_documento=0) then
            insert into moises.persona_documento(id_persona,id_tipodocumento,num_documento,es_activo)
            VALUES (p_id_persona,v_id_tipo_documento,v_num_documento,1);
        ELSE
            select coalesce((select Distinct 1 from moises.persona_documento where id_persona=p_id_persona AND NUM_DOCUMENTO = v_num_documento),0) into STRICT p_res_persona_doc_condicion;
            if (p_res_persona_doc_condicion=0) THEN

                update moises.persona_documento
                set id_tipodocumento=v_id_tipo_documento,num_documento=v_num_documento,es_activo=1
                where id_persona=p_id_persona;
            ELSE

                update moises.persona_documento
                set id_tipodocumento=v_id_tipo_documento,num_documento=v_num_documento,es_activo=1
                where id_persona=p_id_persona AND NUM_DOCUMENTO  = v_num_documento;
            end if;
        end if;

        select coalesce((select Distinct 1 from moises.persona_natural where id_persona=p_id_persona),0) into STRICT p_res_persona_natural;

        if (p_res_persona_natural=0) then

            insert into moises.persona_natural(id_persona,id_tipotratamiento,id_tipoestadocivil,id_tipopais,id_ubigeo,id_tiporeligion,id_tipodocumento,num_documento,sexo,fec_nacimiento,celular,correo,id_nacionalidad)
            VALUES(p_id_persona,1,
                   (SELECT id_tipoestadocivil from moises.tipo_estado_civil where nombre_corto=v_estado_civil),
                   (select id_tipopais from moises.tipo_pais where iso_a2=v_nacionalidad),
                   (select id_ubigeo from moises.ubigueo where depto=substr(v_ubigeo,0,2) and pvcia=substr(v_ubigeo,3,2) and ditto=substr(v_ubigeo,-2) ),
                   (select id_tiporeligion from moises.tipo_religion where codigo=v_cod_religion),
                   v_id_tipo_documento,v_num_documento,
                   (select sexo from moises.tipo_sexo where codigo=v_sexo),
                   v_fecha_nacimiento,v_celular,v_correo,
                   (select id_tipopais from moises.tipo_pais where iso_a2=v_nacionalidad));

            select ft_max_codigo_conv(1,V_CONVOCATORIA) into STRICT p_codigo_universitario;

            insert into moises.persona_natural_alumno(id_persona,codigo)
            VALUES (p_id_persona,p_codigo_universitario);

            insert into acad_alumno_plan(id_persona,id_plan_programa,id_sede,estado,id_usuario_reg,fecha_registro)
            VALUES (p_id_persona,v_id_plan_programa,v_id_sede,1,5,clock_timestamp());

        else

            update moises.persona_natural
            set id_tipotratamiento=1,
                id_tipoestadocivil=(SELECT id_tipoestadocivil from moises.tipo_estado_civil where nombre_corto=v_estado_civil),
                id_tipopais=(select id_tipopais from moises.tipo_pais where iso_a2=v_nacionalidad),
                id_ubigeo=(select id_ubigeo from moises.ubigueo where depto=substr(v_ubigeo,0,2) and pvcia=substr(v_ubigeo,3,2) and ditto=substr(v_ubigeo,-2) ),
                id_tiporeligion=(select id_tiporeligion from moises.tipo_religion where codigo=v_cod_religion),
                id_tipodocumento=v_id_tipo_documento,num_documento=v_num_documento,
                sexo=(select sexo from moises.tipo_sexo where codigo=v_sexo),
                fec_nacimiento=v_fecha_nacimiento,
                celular=v_celular,correo=v_correo,
                id_nacionalidad=(select id_tipopais from moises.tipo_pais where iso_a2=v_nacionalidad)
            where id_persona=p_id_persona;

            select coalesce((select Distinct 1 from moises.persona_natural_alumno where id_persona=p_id_persona),0) into STRICT p_res_persona_alumno;

            if (p_res_persona_alumno=0) then

                select ft_max_codigo_conv(1,V_CONVOCATORIA) into STRICT p_codigo_universitario;

                insert into moises.persona_natural_alumno(id_persona,codigo)
                VALUES (p_id_persona,p_codigo_universitario);

                insert into acad_alumno_plan(id_persona,id_plan_programa,id_sede,estado,id_usuario_reg,fecha_registro)
                VALUES (p_id_persona,v_id_plan_programa,v_id_sede,1,5,clock_timestamp());

            else

                select coalesce((select (case when codigo like '3%' then 0
                                         when codigo like '4%' then 0
                                         else IS_NUMBER(codigo) end) from moises.persona_natural_alumno where id_persona=p_id_persona),0) into STRICT p_res_codigo;

                if (p_res_codigo=0) then

                    select ft_max_codigo_conv(1,V_CONVOCATORIA) into STRICT p_codigo_universitario;
                    update moises.persona_natural_alumno set codigo=p_codigo_universitario where id_persona=p_id_persona;

                end if;

                select Ape.Id_Nivel_Ensenanza into STRICT p_id_nivel_ensenanza from acad_Plan_Programa app inner join Acad_Programa_Estudio ape on Ape.Id_Programa_Estudio=App.Id_Programa_Estudio where Id_Plan_Programa=v_id_plan_programa;

                update Acad_Alumno_Plan
                set estado=0, id_usuario_act=5, fecha_actualizacion=clock_timestamp()
                where estado='1' and (id_persona,id_plan_programa) in (SELECT id_persona,id_plan_programa from Vw_Alumno_Plan_Programa where id_persona=p_id_persona and Id_Nivel_Ensenanza in (p_id_nivel_ensenanza,9) );

                update Acad_Alumno_Plan
                set estado=1, id_usuario_act=5, fecha_actualizacion=clock_timestamp()
                where estado='0' and
                        id_persona=p_id_persona and id_plan_programa=v_id_plan_programa;

                insert into acad_alumno_plan(id_persona,id_plan_programa,id_sede,estado,id_usuario_reg,fecha_registro)
                SELECT p_id_persona,v_id_plan_programa,v_id_sede,1,5,clock_timestamp()
                where (p_id_persona,v_id_plan_programa,v_id_sede) not in (SELECT id_persona,id_plan_programa,id_sede from Vw_Alumno_Plan_Programa where id_persona=p_id_persona and Id_Nivel_Ensenanza=p_id_nivel_ensenanza);


            end if;

        end if;

        select coalesce((select id from eliseo.users where id=p_id_persona),0) into STRICT p_res_user;

        if (p_res_user=0) then

            p_res:='S';

            insert into eliseo.users(ID,EMAIL,CONTRASENHA,NAME,ID_PERFIL,FECHA_CREATE)
            VALUES (p_id_persona,ft_newformat_users(p_id_persona ),'3ca413e61535cf2017496b87412aa332072053f8',v_nombre||' '||v_paterno||' '||v_materno,2,clock_timestamp());

            select coalesce((select Distinct 1 from eliseo.conta_entidad_usuario where id_persona=p_id_persona),0) into STRICT contar_entidad;
            if (contar_entidad=0) then
                insert into eliseo.conta_entidad_usuario(ID_ENTIDAD,ID_PERSONA,ESTADO)
                VALUES (7124,p_id_persona,1);
            end if;

            select coalesce((select Distinct 1 from eliseo.LAMB_USERS_DEPTO where id=p_id_persona),0) into STRICT contar_depto;
            if (contar_depto=0) then
                insert into eliseo.LAMB_USERS_DEPTO(ID,ID_ENTIDAD,ID_DEPTO,ESTADO,ACTIVO)
                VALUES (p_id_persona,7124,(SELECT CASE WHEN id_sede=2 THEN 5 WHEN id_sede=3 THEN 6  ELSE 1 END  from Vw_Alumno_Plan_Programa where id_persona=p_id_persona),1,1);
            end if;


        else

            p_res:='N';

        end if;

        insert into eliseo.conta_entidad_usuario(ID_ENTIDAD,ID_PERSONA,ESTADO)
        SELECT 7124,p_id_persona,1

        where p_id_persona not in (SELECT id_persona from eliseo.conta_entidad_usuario where ID_PERSONA=p_id_persona and ID_ENTIDAD=7124);


        update eliseo.LAMB_USERS_DEPTO set estado='0' where id=p_id_persona;

        update eliseo.LAMB_USERS_DEPTO set estado='1' where id=p_id_persona and ID_DEPTO=(SELECT min(CASE WHEN id_sede=2 THEN 5 WHEN id_sede=3 THEN 6  ELSE 1 END ) from Vw_Alumno_Plan_Programa where id_persona=p_id_persona and estado='1');

        insert into eliseo.LAMB_USERS_DEPTO(ID,ID_ENTIDAD,ID_DEPTO,ESTADO,ACTIVO)
        SELECT p_id_persona,7124,(SELECT CASE WHEN id_sede=2 THEN 5 WHEN id_sede=3 THEN 6  ELSE 1 END  from Vw_Alumno_Plan_Programa where id_persona=p_id_persona),1,1

        where p_id_persona not in (select id from eliseo.users where id=p_id_persona);

        insert into eliseo.LAMB_USUARIO_ROL(ID_ROL,ID_PERSONA,ID_ENTIDAD)
        SELECT 44,p_id_persona,7124

        where p_id_persona not in (SELECT id_persona from eliseo.LAMB_USUARIO_ROL where ID_PERSONA=p_id_persona and ID_ROL=44 and ID_ENTIDAD=7124);

    end if;

    if (v_id_tipo_beca_estatal IS NOT NULL AND v_id_tipo_beca_estatal::text <> '') then
        insert into ACAD_ALUMNO_BECA(ID_PERSONA,ID_TIPO_REQUISITO_BECA,ID_TIPO_BECA_ESTATAL,ESTADO,ID_USUARIO_REG,FECHA_REGISTRO)
        SELECT p_id_persona,33,v_id_tipo_beca_estatal,1,5,clock_timestamp()

        where p_id_persona not in (SELECT id_persona from ACAD_ALUMNO_BECA where ID_PERSONA=p_id_persona);
    end if;

    commit;
    open CURSOR FOR
        SELECT distinct p_res new_user,pna.codigo codigo_universitario,p.id_persona,u.contrasenha password,u.email username,p.nombre first_name,
                        p.paterno||' '||p.materno last_name,pn.correo email,pn.num_documento doc_number, '0'||pn.id_tipodocumento doc_type,1 as resultado, null as code,null as msg_error
        from moises.persona p inner join moises.persona_natural pn on pn.id_persona=p.id_persona
                              inner join moises.persona_natural_alumno pna on pna.id_persona=p.id_persona
                              inner join eliseo.users u on u.id=p.id_persona
        where p.id_persona =p_id_persona;

EXCEPTION

    WHEN OTHERS THEN
        l_code := SQLSTATE;
        l_error := SUBSTR(SQLERRM, 1, 64);

        open CURSOR FOR
            SELECT distinct p_res new_user,pna.codigo codigo_universitario,p.id_persona,u.contrasenha password,u.email username,p.nombre first_name,
                            p.paterno||' '||p.materno last_name,pn.correo email,pn.num_documento doc_number, '0'||pn.id_tipodocumento doc_type,0 as resultado, l_code as code,l_error as msg_error
            from moises.persona p inner join moises.persona_natural pn on pn.id_persona=p.id_persona
                                  inner join moises.persona_natural_alumno pna on pna.id_persona=p.id_persona
                                  inner join eliseo.users u on u.id=p.id_persona
            where p.id_persona =p_id_persona;


end;
$body$
LANGUAGE PLPGSQL
;
-- REVOKE ALL ON PROCEDURE david.pcd_create_alumno_admision_n ( V_ID_TIPO_DOCUMENTO integer, V_NUM_DOCUMENTO text, V_NOMBRE text, V_PATERNO text, V_MATERNO text, V_ESTADO_CIVIL text, V_NACIONALIDAD text, V_UBIGEO integer, V_COD_RELIGION text, V_SEXO text, V_FECHA_NACIMIENTO timestamp(0), V_CELULAR text, V_CORREO text, V_ID_PLAN_PROGRAMA integer, V_ID_SEDE integer, V_ID_TIPO_BECA_ESTATAL integer, V_CONVOCATORIA text, V_OPCION integer, out CURSOR REFCURSOR ) FROM PUBLIC;

