CREATE OR REPLACE PROCEDURE david.pcd_delete_sp_plan (p_id_plan integer) AS $body$
BEGIN
  delete FROM ACAD_PRE_REQUISITO where Id_Pre_Requisito in (
  SELECT Pr.Id_Pre_Requisito
  from Acad_Pre_Requisito pr inner join Acad_Plan_Curso apc on Apc.Id_Plan_Curso=Pr.Id_Plan_Curso
  where Apc.Id_Plan=p_id_plan);

  
  delete FROM acad_plan_curso 
  where id_plan_curso in (
  SELECT pc.id_plan_curso
  from acad_plan_curso pc 
  where pc.Id_Plan=p_id_plan and (pc.Id_Plan_Curso_Electivo IS NOT NULL AND pc.Id_Plan_Curso_Electivo::text <> '') );

  
  delete FROM acad_plan_curso where id_plan_curso in (
  SELECT pc.id_plan_curso
  from acad_plan_curso pc 
  where pc.Id_Plan=p_id_plan and coalesce(pc.Id_Plan_Curso_Electivo::text, '') = '' );

  commit;

end;
$body$
LANGUAGE PLPGSQL
;
