CREATE OR REPLACE PROCEDURE david.pcd_reg_candado_ingles_act (v_activo integer) AS $body$
DECLARE


  semestre CURSOR FOR
  SELECT id_semestre
  from acad_semestre
  where estado='1' and substr(semestre,-2) in ('-1','-2','-0','-M') and substr(semestre,0,4)=to_char(clock_timestamp(),'YYYY')
;

  
BEGIN
  
    for cc in semestre loop
      CALL pcd_reg_candado_ingles_sem(cc.id_semestre);
    end loop;

  commit;
  end;
$body$
LANGUAGE PLPGSQL
;
