CREATE OR REPLACE PROCEDURE david.pcd_update_state_mat_programa () AS $body$
DECLARE

	matricula_programa_list CURSOR FOR SELECT * FROM david.MATRICULA_PROGRAMA WHERE ESTADO = 1;
	fecha timestamp(0);
	fecha_fin_programa_v timestamp(0);
	id_matricula_programa_v bigint;
BEGIN
	
	FOR item IN matricula_programa_list LOOP

		SELECT mp.ID_MATRICULA_PROGRAMA, coalesce(MP.fecha_fin, AMD.fecha_fin) AS fecha_fin INTO STRICT id_matricula_programa_v, fecha_fin_programa_v FROM david.MATRICULA_PROGRAMA mp
		JOIN david.ACAD_MATRICULA_DETALLE amd ON mp.ID_MATRICULA_DETALLE = amd.ID_MATRICULA_DETALLE
		WHERE mp.ID_MATRICULA_PROGRAMA = item.id_matricula_programa;
	
		IF date_trunc('day', fecha_fin_programa_v) < date_trunc('day', clock_timestamp()) THEN
		  -- DBMS_OUTPUT.PUT_LINE(id_matricula_programa_v || ' - ' || fecha_fin_programa_v);
		  UPDATE david.MATRICULA_PROGRAMA SET estado = '0' WHERE ID_MATRICULA_PROGRAMA = id_matricula_programa_v;
      commit;
		END IF;
	
	END LOOP;

END;
$body$
LANGUAGE PLPGSQL
;
-- REVOKE ALL ON PROCEDURE david.pcd_update_state_mat_programa () FROM PUBLIC;

