CREATE OR REPLACE PROCEDURE david.pcd_validacion_becas_cc (p_id_persona integer,p_id_plan_programa integer,p_id_semestre integer,p_id_tipo_requisito_beca integer, out CURSOR REFCURSOR) AS $body$
BEGIN

  open CURSOR FOR
  SELECT (case when max(promedio)='A' and max(deuda)='A' and max(regular)='A' and max(ciclo)='A' then 'NO' else 'SI' end ) as bloqueo,
  (case when max(promedio)='A' and max(deuda)='A' and max(regular)='A' and max(ciclo)='A' then 'OK' else 
        CASE WHEN max(promedio)='A' THEN ''  ELSE max(promedio)||',' END ||CASE WHEN max(deuda)='A' THEN ''  ELSE max(deuda)||',' END ||
        CASE WHEN max(regular)='A' THEN ''  ELSE max(regular)||',' END ||CASE WHEN max(ciclo)='A' THEN ''  ELSE max(ciclo)||',' END  end ) as mensaje
  
  from (SELECT 'A' promedio, 'A'/*CASE WHEN ft_tiene_deuda(p_id_persona,p_id_semestre)=1 THEN 'El saldo no esta en tu estado de cuenta , no cumple con lo requerido para  avanzar en tu solicitud'  ELSE 'A' END */ deuda, 
        'A' regular, 'A' ciclo
        
        
union

        select max(promedio) promedio,'A' deuda, max(regular) regular, max(ciclo) ciclo
        from (select CASE WHEN automatico='A2' THEN ''||CASE WHEN ft_tiene_aprobado(p_id_persona,p_id_plan_programa)=1 THEN 'El Promedio Ponderado minimo requerido no ha sido alcanzado'  ELSE 'A' END   ELSE 'A' END  promedio,
              CASE WHEN automatico='A4' THEN CASE WHEN ft_tiene_desbloqueo(p_id_persona,p_id_plan_programa,p_id_semestre)=1 THEN 'A'  ELSE ''||CASE WHEN ft_tiene_aprobado_ant(p_id_persona,p_id_plan_programa)=1 THEN 'Tiene uno o más cursos desaprobados el ciclo anterior y su promedio final es menor a 14' WHEN ft_tiene_aprobado_ant(p_id_persona,p_id_plan_programa)=2 THEN 'Tiene uno o más cursos desaprobados el ciclo anterior' WHEN ft_tiene_aprobado_ant(p_id_persona,p_id_plan_programa)=3 THEN 'Tiene el 100% de los créditos aprobados en el semestre académico precedente pero no tien un promedio ponderado mínimo de catorce (14)'  ELSE 'A' END  END                 ELSE 'A' END  regular,
              (case when Id_Tipo_Requisito_Beca in (4,11,17,45,48,57,67) then (case when ft_ciclo_plan_alumno(p_id_persona,p_id_plan_programa)<=2 then 'A'
                                                                                    else ''||CASE WHEN ft_tiene_segundociclo(p_id_persona,p_id_plan_programa)=1 THEN 'Aun no puedes solicitar , pues no estas en el ciclo minimo requerido'  ELSE 'A' END  end ) else
                    CASE WHEN automatico='A7' THEN ''||CASE WHEN ft_tiene_tercerciclo(p_id_persona,p_id_plan_programa)=1 THEN 'Aun no puedes solicitar , pues no estas en el ciclo minimo requerido'  ELSE 'A' END   ELSE 'A' END  
                    end)ciclo
              from acad_requisitos_beca where Id_Tipo_Requisito_Beca=p_id_tipo_requisito_beca and estado='1' and Automatico in ('A2','A4','A1','A7') ) alias34 ) alias35;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE david.pcd_validacion_becas_cc (p_id_persona integer,p_id_plan_programa integer,p_id_semestre integer,p_id_tipo_requisito_beca integer, out CURSOR REFCURSOR) FROM PUBLIC;

