CREATE OR REPLACE VIEW vw_acad_persona_carga (id_persona_contrato, semestre, nombre_facultad, programa_estudio, nombre, paterno, materno, id_tipodocumento, num_documento, correo_inst, correo, telefono) AS SELECT apc.ID_PERSONA_CONTRATO ,
as2.CODIGO  AS semestre,
vape.NOMBRE_FACULTAD ,
vape.NOMBRE AS programa_estudio,
p.NOMBRE ,
p.PATERNO ,
p.MATERNO ,
