CREATE OR REPLACE VIEW vw_alumno_plan_programa (id_alumno_plan, id_persona, estado, id_plan_programa, id_plan, nombre_plan, id_sede, sede, id_escuela, escuela, nom_escuela, nombre_escuela, escuela_profesional, id_facultad, nom_facultad, id_programa_estudio, id_tipo_contrato, id_nivel_ensenanza, id_sedearea, graduado, contrato, fecha_registro) AS select aap.id_alumno_plan,aap.Id_Persona,aap.estado,app.Id_Plan_Programa,app.Id_Plan,Ap.Nombre_Plan,
