CREATE OR REPLACE VIEW vw_matricula_programa (id_matricula_programa, semestre_de_programa, semestre_de_matricula, codigo_matricula, sede, nombre, modo_matricula, tipo_nivel_ensenanza, tipo_contrato, tipo_nivel_ensenanza_programa, tipo_contrato_programa, id_matricula_detalle, id_matricula, id_nivel_ensenanza_programa, id_tipo_contrato_programa, id_nivel_ensenanza, id_tipo_contrato, estado, id_semestre, id_programa_estudio) AS SELECT
mp.ID_MATRICULA_PROGRAMA ,
as3.NOMBRE  AS semestre_de_programa,
as2.NOMBRE  AS semestre_de_matricula,
am.CODIGO  AS codigo_matricula,
vape.sede,
vape.nombre,
mc.nombre AS modo_matricula,
tne.nombre AS tipo_nivel_ensenanza,
tc.nombre AS tipo_contrato,
tne2.nombre AS tipo_nivel_ensenanza_programa,
tc2.nombre AS tipo_contrato_programa,
amd.id_matricula_detalle,
am.id_matricula,
vape.id_nivel_ensenanza AS id_nivel_ensenanza_programa,
vape.id_tipo_Contrato AS id_tipo_contrato_programa,
tne.id_nivel_ensenanza,
tc.id_tipo_contrato,
mp.estado,
as2.id_semestre,
vape.id_programa_estudio

FROM MATRICULA_PROGRAMA mp 
INNER JOIN ACAD_SEMESTRE_PROGRAMA asp ON mp.ID_SEMESTRE_PROGRAMA = asp.ID_SEMESTRE_PROGRAMA 
INNER JOIN VW_ACAD_PROGRAMA_ESTUDIO vape ON vape.ID_PROGRAMA_ESTUDIO =asp.ID_PROGRAMA_ESTUDIO 
INNER JOIN ACAD_MATRICULA_DETALLE amd ON amd.ID_MATRICULA_DETALLE = mp.ID_MATRICULA_DETALLE 
INNER JOIN ACAD_MATRICULA am ON am.ID_MATRICULA =amd.ID_MATRICULA 
INNER JOIN MODO_CONTRATO mc ON mc.ID_MODO_CONTRATO = amd.ID_MODO_CONTRATO 
INNER JOIN ACAD_SEMESTRE as2 ON as2.ID_SEMESTRE =am.ID_SEMESTRE 
INNER JOIN ACAD_SEMESTRE as3 ON as3.ID_SEMESTRE = asp.ID_SEMESTRE 
INNER JOIN TIPO_NIVEL_ENSENANZA tne ON tne.ID_NIVEL_ENSENANZA =amd.ID_NIVEL_ENSENANZA 
INNER JOIN TIPO_CONTRATO tc ON tc.ID_TIPO_CONTRATO = amd.ID_TIPO_CONTRATO 
INNER JOIN TIPO_NIVEL_ENSENANZA tne2 ON tne2.ID_NIVEL_ENSENANZA =vape.ID_NIVEL_ENSENANZA 
INNER JOIN TIPO_CONTRATO tc2 ON tc2.ID_TIPO_CONTRATO = vape.ID_TIPO_CONTRATO;

