function ft_alumno_mat_semestre(p_id_persona integer, p_id_plan_programa integer, p_id_semestre integer) RETURN integer AS
  res integer;
BEGIN
  SELECT Distinct 1 into res
  FROM DAVID.VW_ACAD_ALUMNO_CONTRATO
  where id_persona = p_id_persona and Id_Plan_Programa = p_id_plan_programa and id_semestre=p_id_semestre
  and estado='1';
  return res;
end;

