SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION ft_al_semestre_ant( v_id_persona in integer,v_id_escuela in integer,v_semestre in varchar2,v_tipo in varchar2) 
RETURN varchar2 IS
	p_semestre	  varchar2(800);
		   
BEGIN

	select semestre into p_semestre
  from acad_semestre where id_semestre=v_semestre;
  
  select max(Semestre) into p_semestre 
  from Vw_Acad_Alumno_Contrato 
  where id_persona=v_id_persona and Id_Escuela=v_id_escuela
  and Id_semestre<>v_semestre and estado='1' and semestre<p_semestre
  and substr(semestre,-2) in ('-1','-2','-3') and id_modo_contrato=1;
  
  if(v_tipo='ID') then
  
  select ''||id_semestre into p_semestre
  from acad_semestre 
  where semestre=p_semestre;
  
  end if;
       
return p_semestre;

END;

