SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_calcular_ciclo_programa(p_semestre in varchar2,p_id_persona in integer,p_id_plan_programa in integer) 
return number
is
  p_ciclo number;
begin  

  select a.ciclo into p_ciclo
  from(select distinct aca.id_persona, vac.ciclo, sum(vac.credito) 
        from acad_curso_alumno aca inner join vw_acad_carga_academica vac on vac.id_carga_curso=aca.id_carga_curso
              and aca.id_persona=p_id_persona and vac.semestre=p_semestre and aca.id_tipo_condicion<>3 
              and aca.id_plan_programa=p_id_plan_programa and Vac.Origen='O' and aca.estado<>'4'
        group by aca.id_persona, vac.ciclo
        order by 3 desc,ciclo desc) a
  where rownum=1;
  
  return p_ciclo;
  
end;

