SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_calcular_creditos_semestre(p_semestre in varchar2,p_id_persona in integer,p_id_plan_programa in integer) 
return number
is
  p_creditos number;
begin  

  select sum(credito) into p_creditos 
  from Vw_Acad_Curso_Alumno where id_persona=p_id_persona and semestre=p_semestre 
  and id_plan_programa=p_id_plan_programa and id_tipo_condicion<>3 and estado<>'4';
  
  return p_creditos;
  
end;

