SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_calcular_Escuela_docentea(p_id_semestre in integer,p_id_persona in integer) 
return varchar2
is
  p_escuela varchar2(4000);
begin 
  select nom_escuela into p_escuela
  from (
        select Acc.Nom_Escuela,count(*)num 
        from acad_carga_curso_docente accd inner join vw_Acad_Carga_academica acc on Acc.Id_Carga_Curso=accd.id_carga_curso
        where id_semestre=p_id_semestre and origen='O'
        and id_persona=p_id_persona
        group by Acc.Nom_Escuela
        order by 2 desc)
  where rownum=1;
  return p_escuela;
end;

