SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_calcular_filial_docente(p_id_semestre in integer,p_id_persona in integer) 
return varchar2
is
  p_sede varchar2(4000);
begin 
  select Distinct nombre into p_sede
  from (
        select os.nombre,count(*)num 
        from acad_carga_curso_docente accd inner join vw_Acad_Carga_academica acc on Acc.Id_Carga_Curso=accd.id_carga_curso
        inner join ELISEO.Org_Sede os on os.Id_Sede=Acc.Id_Sede
        where id_semestre=p_id_semestre and origen='O'
        and id_persona=p_id_persona
        group by os.nombre
        order by 2 desc)
  where rownum=1;
  return p_sede;
end;

