SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_calcular_grupo_programa(p_semestre in varchar2,p_id_persona in integer,p_id_plan_programa in integer) 
return varchar2
is
  p_grupo varchar2(500);
  p_ciclo number;
begin  

  select Ft_Calcular_Ciclo_Programa(p_semestre,p_id_persona,p_id_plan_programa) into p_ciclo from dual;

  select a.grupo into p_grupo
  from(select distinct aca.id_persona, decode(aca.grupo,null,'Único','0','Único',aca.grupo) grupo, sum(aca.credito) 
        from vw_acad_curso_alumno aca 
              where aca.id_persona=p_id_persona and aca.semestre=p_semestre and aca.id_tipo_condicion<>3 and aca.id_plan_programa=p_id_plan_programa
              and aca.ciclo=p_ciclo and aca.estado<>'4'
        group by aca.id_persona, aca.grupo
        order by 3 desc) a
  where rownum=1;
  
  return p_grupo;
  
end;

