function ft_calcular_grupo(p_semestre in varchar2,p_id_persona in integer,p_id_nivel_ensenanza in integer) 
return varchar2
is
  p_grupo varchar2(500);
begin  

  select a.grupo into p_grupo
  from(select distinct aca.id_persona, nvl(vac.grupo,'Ãšnico') grupo, count(distinct vac.id_carga_curso) 
        from acad_curso_alumno aca inner join vw_acad_carga_academica vac on vac.id_carga_curso=aca.id_carga_curso and vac.id_curso_detalle=aca.id_curso_detalle
              and aca.id_persona=p_id_persona and vac.semestre=p_semestre and aca.id_tipo_condicion<>3 and Vac.Origen='O'
              and Vac.Id_Nivel_Ensenanza=p_id_nivel_ensenanza
        group by aca.id_persona, vac.grupo
        order by 3 desc) a
  where rownum=1;
  
  return p_grupo;
  
end;

