function ft_calcular_promedio_adviser(v_id_carga_curso number, v_id_persona number, v_id_programa_estudio number)
return number
is
    promedio_minimo_cat number;
    promedio_ponderado number;
    promedio_final number;
    ponderado number;
    promedio_res varchar2(200);
begin

    if (v_id_programa_estudio = 77) then
    
        select round(sum(nota*ponderado)/sum(ponderado),2) ,sum(ponderado)
        into promedio_ponderado,ponderado
        from (select Ace.Descripcion,Ace.Fecha_Evaluacion,Ace.Ponderado ponderado_ref,nvl(Aae.Nota,0) nota,
              (case when to_date(substr(Ace.Fecha_Evaluacion,0,10),'YYYY-MM-DD')<=to_date(substr(sysdate,0,10),'YYYY-MM-DD') or nota is not null then Ace.Ponderado else 0 end) ponderado
              from acad_carga_evaluacion ace 
              left join acad_alumno_evaluacion aae on ace.id_carga_evaluacion = aae.id_carga_evaluacion and aae.id_persona = v_id_persona
              where ace.id_carga_curso = v_id_carga_curso)
        where ponderado <>0;  
        
        select min(round(sum(nota*ponderado)/sum(ponderado),2)) into  promedio_minimo_cat
        from (
        select ae.id_tipo_estrategia,Ace.Descripcion,Ace.Fecha_Evaluacion,Ace.Ponderado ponderado_ref,nvl(Aae.Nota,0) nota,
        (case when to_date(substr(Ace.Fecha_Evaluacion,0,10),'YYYY-MM-DD')<=to_date(substr(sysdate,0,10),'YYYY-MM-DD') or nota is not null then Ace.Ponderado else 0 end) ponderado
        from acad_carga_evaluacion ace 
        left join acad_alumno_evaluacion aae on ace.id_carga_evaluacion = aae.id_carga_evaluacion and aae.id_persona = v_id_persona
        left join acad_estrategia ae on ae.id_estrategia = ace.id_estrategia
        where ace.id_carga_curso = v_id_carga_curso
        )
        where ponderado <>0
        group by id_tipo_estrategia;

        if (promedio_minimo_cat < 12.5) then
            promedio_final := promedio_minimo_cat;
        else
            promedio_final := promedio_ponderado;
        end if;
        
    else
    
        select round(sum(nota*ponderado)/sum(ponderado),2) , sum(ponderado)
        into promedio_final,ponderado
        from (select Ace.Descripcion,Ace.Fecha_Evaluacion,Ace.Ponderado ponderado_ref,nvl(Aae.Nota,0) nota,
              (case when to_date(substr(Ace.Fecha_Evaluacion,0,10),'YYYY-MM-DD')<=to_date(substr(sysdate,0,10),'YYYY-MM-DD') or nota is not null then Ace.Ponderado else 0 end) ponderado
              from acad_carga_evaluacion ace 
              left join acad_alumno_evaluacion aae on ace.id_carga_evaluacion = aae.id_carga_evaluacion and aae.id_persona = v_id_persona
              where ace.id_carga_curso = v_id_carga_curso)
        where ponderado <>0
        and rownum=1;   
        
    end if;

    if(promedio_final is null) then
      promedio_res:=null;
    elsif(promedio_final=0) then
      promedio_res:='0';
    else
      promedio_res:=promedio_final||' ('||ponderado||'%)';
    end if;
    
    return promedio_final;
end;

