function ft_cantidad_cursos_repetidos(v_id_persona integer, v_id_plan_programa integer, v_id_semestre integer, v_tipo varchar2)
RETURN number IS
  p_res	  number;		 

BEGIN
    
    if(v_tipo='CN') then
    
      select sum(decode(ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle),'S',0,credito)) into p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4';
      
    elsif(v_tipo='SN') then
    
      select sum(decode(ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle),'S',0,1))  into p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4';
    
    elsif(v_tipo='CR') then
    
      select sum(decode(ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle),'S',credito,0)) into p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4';
      
    elsif(v_tipo='SR') then
    
      select sum(decode(ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle),'S',1,0))  into p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4';
      
    elsif(v_tipo='SR2V') then
    
      select sum(decode(ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle),'S',1,0))  into p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4'
        and ft_cant_cur_veces_repetidos(id_persona, id_plan_curso)=2;
      
    elsif(v_tipo='SR3V') then
    
      select sum(decode(ft_verif_cursos_repetidos(id_persona, v_id_plan_programa, id_curso_detalle),'S',1,0))  into p_res
      from vw_acad_curso_alumno where id_persona=v_id_persona and id_semestre=v_id_semestre and id_tipo_condicion<>3 
        and id_plan_programa=v_id_plan_programa and estado<>'4'
        and ft_cant_cur_veces_repetidos(id_persona, id_plan_curso)=3;
        
        
    end if;

  
  return p_res;

END;

