function ft_cant_cur_veces_repetidos(v_id_persona integer, v_id_plan_curso integer)
RETURN number IS
  p_id_area	  number;	
  p_res	  number;		 

BEGIN

  select nvl((select count(*) 
  from vw_acad_curso_alumno aca
  where id_plan_curso=v_id_plan_curso and id_tipo_condicion=2),0) into p_res
  from dual;

  
  return p_res;

END;

