function ft_cant_horas_curso_docente(v_id_persona integer,v_id_carga_curso integer)
RETURN number IS
  p_res	  number;		 
  p_nivel_ensenaza	  number;
  p_id_escuela	  number;

BEGIN

  select nvl((select sum(horas_mes*decode(accd.factor,0,1,accd.factor))/16 
              from tb_pcd_horas_mes_ccd thm inner join acad_carga_curso_docente accd on accd.id_carga_curso_docente=thm.id_carga_curso_docente 
              where accd.Id_Carga_Curso=v_id_carga_curso and accd.id_persona=v_id_persona)
          ,0)
  into p_res 
  from dual;      
  
  return p_res;

END;

