SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_cant_horas_mes_cd(v_id_comision integer,v_mes varchar2,v_tipo varchar2)
RETURN number IS
  p_res	  number;		 
  p_nivel_ensenaza	  number;
  p_id_escuela	  number;

BEGIN



  if(v_tipo='CM') then
    select nvl((select horas_laborales
                from acad_persona_comision apcc inner join Acad_Persona_Contrato apc on Apc.Id_Persona_Contrato=Apcc.Id_Persona_Contrato 
                where apcc.Id_Persona_Comision=v_id_comision and to_number(v_mes) Between to_number(to_char(apc.fecha_inicio,'MM')) and to_number(to_char(apc.fecha_fin,'MM')))
            ,0)
    into p_res 
    from dual;
  elsif(v_tipo='CR') then
  
    select Aca.Id_Nivel_Ensenanza,Aca.Id_Escuela into p_nivel_ensenaza,p_id_escuela
    from vw_acad_carga_academica aca inner join Acad_Carga_Curso_Docente accd on Accd.Id_Carga_Curso=Aca.Id_Carga_Curso and Aca.Origen='O'
    where Accd.Id_Carga_Curso_Docente=v_id_comision;
    
    if(p_nivel_ensenaza=2 or p_id_escuela=83) then
    
      select nvl((select horas_mes/4 from tb_pcd_horas_mes_ccd where Id_Carga_Curso_Docente=v_id_comision and mes=v_mes)
              ,0)
      into p_res 
      from dual;
      
    else
    
      select nvl((select ft_num_horas_curso_doc( accd.Id_Carga_Curso_Docente )
                  from Acad_Carga_Curso_Docente accd inner join Acad_Modulo_Detalle amd on Amd.Id_Modulo_Detalle=Accd.Id_Modulo_Detalle 
                  where accd.Id_Carga_Curso_Docente=v_id_comision and to_number(v_mes) Between to_number(to_char(amd.fecha_inicio,'MM')) and to_number(to_char(amd.fecha_fin,'MM')))
              ,0)
      into p_res 
      from dual;
    
    end if;
    
  end if;
  
  return p_res;

END;

