function ft_carreras_persona(v_id_persona integer) 
return varchar2
is
  p_escuela varchar2(4000);
begin  
  select wm_concat(p_escuela) into p_escuela
  from (select Distinct nombre_escuela p_escuela from Vw_Acad_Alumno_Contrato 
         where id_persona=v_id_persona and estado='1' and Id_Tipo_Contrato in (1,2,4,5));
  return p_escuela;
end;

