FUNCTION FT_CONCATENADO_RESPONSABLE_FIN ( V_ID_PERSONA IN NUMBER ) RETURN VARCHAR2 AS 

p_res varchar2(4000);

BEGIN

  select Resp_Financiero||' - Celular: '||nvl(Celular_Resp,'Sin Celular')||' - Correo: '||nvl(Correo_Resp,'Sin Correo') into p_res
  from(
  select id_semestre,Id_Resp_Financiero,Resp_Financiero,Celular_Resp,Correo_Resp from vw_acad_alumno_contrato where id_persona=V_ID_PERSONA and estado='1'
  order by id_semestre desc)
  where rownum=1;
  
  return p_res;
  
end;

