function ft_creditosm_semestre_alumno(v_id_persona integer, v_id_plan_programa integer, v_id_semestre integer)
RETURN number IS
  p_creditos	  number;		 

BEGIN
  select sum(credito) into p_creditos
  from Vw_Acad_Curso_Alumno 
  where id_persona=v_id_persona 
  and id_plan_programa=v_id_plan_programa 
  and id_semestre=v_id_semestre
  and estado='1';
  
  return p_creditos;

END;

