SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_creditos_ap_semestre_alumno(v_id_persona integer, v_id_escuela integer, v_id_semestre integer)
RETURN number IS
  p_creditos	  number;		 

BEGIN
  select nvl((select sum(credito)
  from Vw_Acad_Curso_Alumno 
  where id_persona=v_id_persona 
  and id_escuela=v_id_escuela 
  and id_semestre=v_id_semestre
  and estado<>'4' and id_tipo_condicion='1'),0) into p_creditos
  from dual;
  
  return p_creditos;

END;

