FUNCTION       FT_CREDITO_SEM_ALUM_MODO(v_id_persona INTEGER, v_id_plan_programa INTEGER, v_id_semestre INTEGER, V_ID_CURSO_MODO INTEGER, V_ESTADO VARCHAR2)
RETURN number IS
  p_creditos	  number;		 

BEGIN
	
	select sum(credito) into p_creditoS
  from vw_acad_curso_alumno 
  where id_persona=v_id_persona 
  and id_plan_programa=v_id_plan_programa 
  and id_semestre=v_id_semestre and id_tipo_condicion<>3 
  AND ID_CURSO_MODO=V_ID_CURSO_MODO
  and estado<>'4';

RETURN p_creditos;

END;

