SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION FT_CURSO_DOCENTE_HORARIO(HORARIO VARCHAR2,NUM_DIA VARCHAR2,P_ID_HORARIO integer,P_HORAS integer,P_TIPO VARCHAR2)
RETURN VARCHAR2 IS

NUM NUMBER;
INI NUMBER;
NVAL NUMBER;
VAL VARCHAR2(1000);
T_HORARIO VARCHAR2(1000);
T_TURNO VARCHAR2(1000);
T_PERIODO VARCHAR2(1000);
P_HORA_INICIO VARCHAR2(1000);
P_HORA_FIN VARCHAR2(1000);
RES VARCHAR2(1000);

BEGIN

  SELECT ''||CEIL((LENGTH(REPLACE(HORARIO,'-','')))/3) INTO NUM  FROM DUAL;   
  
  INI:=1;
  NVAL:=1;
  
  WHILE INI <= NUM
  LOOP
  
    SELECT SUBSTR(HORARIO,NVAL,1) INTO VAL FROM DUAL;
    
    IF(VAL=NUM_DIA) THEN
      T_HORARIO:=SUBSTR(HORARIO,NVAL+1,2);
      INI:=5000;
    END IF;
    
    INI:=INI+1;
    NVAL:=NVAL+4;
  
  END LOOP;
  
  --RES ;
  
  T_TURNO:=SUBSTR(T_HORARIO,0,1);
  T_PERIODO:=SUBSTR(T_HORARIO,2,1);
  
  IF(P_TIPO='I') THEN
  
    SELECT HORA_INICIO INTO RES FROM ACAD_HORARIO_DETALLE WHERE ID_HORARIO=P_ID_HORARIO AND ID_TIPO_TURNO=T_TURNO AND PERIODO=T_PERIODO;
    
  ELSIF(P_TIPO='F') THEN    
  
    SELECT NVL((SELECT HORA_FIN FROM ACAD_HORARIO_DETALLE WHERE ID_HORARIO=P_ID_HORARIO AND ID_TIPO_TURNO=T_TURNO AND PERIODO=T_PERIODO+(P_HORAS-1)),'X')INTO RES FROM DUAL;
   
    IF(RES='X') THEN
    
      SELECT NVL((SELECT (HORA_FIN) FROM ACAD_HORARIO_DETALLE WHERE ID_HORARIO=P_ID_HORARIO AND ID_TIPO_TURNO=(T_TURNO+1) AND PERIODO=P_HORAS-(FTMAX_PERIODO(P_ID_HORARIO,T_TURNO)-T_PERIODO)),'Z')INTO RES FROM DUAL    ;
      T_TURNO:=(T_TURNO+1);     
   
      IF(RES='Z') THEN
      
        SELECT (HORA_FIN) INTO RES FROM ACAD_HORARIO_DETALLE WHERE ID_HORARIO=P_ID_HORARIO AND ID_TIPO_TURNO=(T_TURNO+1) AND PERIODO=P_HORAS-((FTMAX_PERIODO(P_ID_HORARIO,T_TURNO-1)+FTMAX_PERIODO(P_ID_HORARIO,T_TURNO))-T_PERIODO);
        T_TURNO:=(T_TURNO+1); 
      
      END IF;
    
    END IF;
    
  END IF;
  
  
  IF(T_TURNO=1) THEN
    SELECT (CASE WHEN (SUBSTR(RES,0,2))=1 THEN TO_NUMBER(SUBSTR(RES,0,2))+12 ELSE TO_NUMBER(SUBSTR(RES,0,2)) END)||SUBSTR(RES,-3) INTO RES FROM DUAL;
  ELSE
    SELECT (CASE WHEN (SUBSTR(RES,0,2))<12 THEN TO_NUMBER(SUBSTR(RES,0,2))+12 ELSE TO_NUMBER(SUBSTR(RES,0,2)) END)||SUBSTR(RES,-3) INTO RES FROM DUAL;
  END IF;
  
  
  
  
  RETURN RES;

END ;

