function ft_curso_plan_equialente(id_plan_curso_origen in integer, id_plan_curso_destino in integer)
 RETURN INTEGER AS 
  
  v_num integer :=0;
    
 begin 
    select nvl((select 1 
                from acad_plan_curso apco 
                where id_plan_curso=id_plan_curso_destino and 
                Exists (select 1 from acad_plan_curso apc 
                        inner join Vw_Curso_Equivalente ce on ce.Id_Curso_Detalle=apc.Id_Curso_Detalle
                        where id_plan_curso=id_plan_curso_origen and Ce.Curso_Equiv=apco.id_curso_detalle)),0)into v_num
    from dual ;
    
    return v_num;

 end;

