SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION ft_desaprobado_ciclo_ant(v_id_persona in integer,v_id_escuela in integer, v_tipo in varchar2) 
RETURN varchar2 IS

	p_semestre	  varchar2(800);
  p_num	  varchar2(800);
  p_desa	  varchar2(800);
  p_res	  varchar2(800);
		   
BEGIN

  select semestre,num,num_desa into p_semestre,p_num,p_desa
	from (select vca.semestre,
        count(aca.id_tipo_condicion) num, sum(decode(aca.id_tipo_condicion,2,1,0)) num_desa
        from acad_curso_alumno aca inner join vw_acad_carga_academica vca on Vca.Id_Carga_Curso=Aca.Id_Carga_Curso and aca.id_persona=v_id_persona
                                    and substr(vca.semestre,-2) in ('-1','-2') and aca.estado='1'
        inner join Acad_Plan_Curso apc on Apc.Id_Plan_Curso=Vca.Id_Plan_Curso
        inner join Vw_Acad_Plan vap on Vap.Id_Plan=apc.id_plan and vap.id_escuela=v_id_escuela
        group by vca.semestre
        order by vca.semestre desc)
  where rownum=1;
  
  if(v_tipo='t') then
  
    if(p_desa>0) then
      p_res:='S';
    else
      p_res:='N';
    end if;
  else  
  
    if (v_tipo='p') then
    
      if(((p_num/2)-p_desa)<0) then
        p_res:='S';
      else
        p_res:='N';
      end if;
      
    end if;
    
  end if;
       
return p_res;

END ft_desaprobado_ciclo_ant;

