function ft_desaprobado_ciclo(v_id_persona integer, v_id_semestre integer, v_id_nivel_ensenanza integer)
RETURN number IS
  p_cantidad	  number;		 

begin

  select count(*) into p_cantidad
  from acad_curso_alumno aca inner join Acad_Carga_Curso acc on Acc.Id_Carga_Curso=Aca.Id_Carga_Curso
  inner join Acad_Semestre_Programa asp on Asp.Id_Semestre_Programa=Acc.Id_Semestre_Programa
  inner join acad_programa_estudio ape on Ape.Id_Programa_Estudio=Asp.Id_Programa_Estudio
  where aca.id_persona=v_id_persona and aca.id_tipo_condicion=2 
        and Asp.Id_Semestre=v_id_semestre and Ape.Id_Nivel_Ensenanza=v_id_nivel_ensenanza;

  return p_cantidad;

end;

