FUNCTION ft_det_escuela_anterior(p_id_persona INTEGER,p_id_escuela_act INTEGER,p_id_nivel_ensenanza INTEGER, p_tipo varchar2)
RETURN Varchar2 IS 

p_escuela Varchar2(2000);
p_fecha Varchar2(2000);
p_res Varchar2(2000);

begin


  select Nombre_Certificado,to_char(FECHA,'DD/MM/YYYY')  into p_escuela, p_fecha
  from (
        select (select max(Fecha_Registro) fecha_registro
        from Vw_Alumno_Plan_Programa b
                  where b.id_persona=a.id_persona and b.id_escuela=p_id_escuela_act and estado='1' group by b.id_persona, b.id_escuela) fecha ,Nombre_Certificado
        from Vw_Alumno_Plan_Programa a inner join Vw_Acad_Programa_Estudio ape on Ape.Id_Programa_Estudio=A.Id_Programa_Estudio
        where a.id_persona=p_id_persona and a.id_escuela<>p_id_escuela_act and a.Id_Nivel_Ensenanza = p_id_nivel_ensenanza
        and a.Nombre_Plan||A.Fecha_Registro<(select max(Nombre_Plan||Fecha_Registro) fecha_registro from Vw_Alumno_Plan_Programa b
                              where b.id_persona=a.id_persona and b.id_escuela=p_id_escuela_act and estado='1' group by b.id_persona, b.id_escuela)
        order by a.Fecha_Registro desc,a.Nombre_Plan desc)
  where rownum=1;
  
  if(p_tipo='N') then
    p_res := p_escuela;
  else
    p_res := p_fecha;
    
  END IF;
  
  RETURN p_res;

END;

