SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_deuda_alumno_N(v_id_persona integer, v_anio integer)
RETURN varchar2 IS
  deuda	  varchar2(500);		 

BEGIN

DECLARE
  v_codigo varchar2(100) := '';
  
  BEGIN


    select codigo into v_codigo from moises.persona where id_persona=v_id_persona;

    SELECT decode(decode(LENGTH(SIGN(NVL(SUM(TOTAL),0))),2,'',0)||SIGN(NVL(SUM(TOTAL),0)),'-1','a Favor : ','01','Debe : ','')
    ||NVL(ABS(SUM(TOTAL)),0) into deuda
    FROM (
      SELECT TOTAL
      FROM eliseo.VW_SALES_MOV
      WHERE ID_ENTIDAD = 7124 AND ID_DEPTO = '1' AND ID_ANHO = v_anio AND ID_CLIENTE = v_id_persona AND ID_TIPOVENTA IN (1,2,3)
      UNION ALL
      SELECT SUM(IMPORTE)*DECODE(SIGN(SUM(IMPORTE)),1,-1,0) AS TOTAL
      FROM eliseo.VW_SALES_ADVANCES
      WHERE ID_ENTIDAD = 7124 AND ID_DEPTO = '1' AND ID_ANHO = v_anio AND ID_CLIENTE = v_id_persona  
    );
  
  return deuda;
  
  END;

end;

