SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function FT_ENOC_CARGO(P_ID_PERSONA in INTEGER) 
return varchar2
is
  p_resultado varchar2(500);
begin

  SELECT NVL((SELECT MIN(UPPER(P.NOMBRE)) 
      FROM ENOC.Vw_Trabajador T INNER JOIN ENOC.PLLA_PUESTO P ON P.ID_PUESTO=T.ID_PUESTO 
      WHERE ID_PERSONA=P_ID_PERSONA AND T.ID_ENTIDAD='7124'),'DOCENTE') INTO p_resultado
  FROM DUAL;
  
  RETURN p_resultado;
  
END;

