function FT_ENOC_FECHA_INGRESO(P_ID_PERSONA in INTEGER) 
return varchar2
is
  p_resultado DATE;
begin

      SELECT MIN(T.Fecha_Ingreso) INTO p_resultado
      FROM ENOC.Vw_Trabajador T INNER JOIN ENOC.PLLA_PUESTO P ON P.ID_PUESTO=T.ID_PUESTO 
      WHERE ID_PERSONA=P_ID_PERSONA AND T.ID_ENTIDAD='7124';
      
  RETURN p_resultado;
END;

