SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION ft_escuela_anterior(p_id_persona INTEGER,p_id_escuela_act INTEGER)
RETURN number IS 

p_id_escuela number;

begin

  select Distinct id_escuela into p_id_escuela
  from (
        select id_escuela from Vw_Alumno_Plan_Programa where id_persona=p_id_persona and id_escuela<>p_id_escuela_act and Id_Nivel_Ensenanza in (1,2)
        order by Fecha_Registro desc)
  where rownum=1;
  
  return p_id_escuela;

end;

