SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function             ft_escuela_profesional_alumno(p_id_persona in integer) 
return varchar2
is
  p_escuela varchar2(500);
begin
    /*select Nom_Escuela into p_escuela
    from (select Aap.Nom_Escuela from Vw_Alumno_Plan_Programa aap
          where aap.id_persona=p_id_persona and aap.estado=1
          order by Aap.Id_Nivel_Ensenanza,Aap.Nombre_Plan desc)
    where rownum=1;*/
    SELECT Distinct vpp.escuela_profesional into p_escuela
    FROM DAVID.VW_ALUMNO_PLAN_PROGRAMA VPP 
    WHERE  ID_PERSONA=P_ID_PERSONA
    AND ID_PLAN_PROGRAMA = (SELECT  ID_PLAN_PROGRAMA
                            FROM (SELECT ID_PLAN_PROGRAMA,ID_PERSONA FROM DAVID.VW_ALUMNO_PLAN_PROGRAMA WHERE ESTADO=1 ORDER BY LPAD(decode(ID_NIVEL_ENSENANZA,1,9,2,9,3,9,4,9,1),2,'0')||NOMBRE_PLAN DESC)
                            WHERE ID_PERSONA=VPP.ID_PERSONA AND ROWNUM=1);

    return p_escuela;


end;

