FUNCTION ft_evidencia_aprendizaje(
    p_id_carga_unidad number 
) 

RETURN varchar2
IS
    r_descripcion varchar2(4000) ;
BEGIN

SELECT 
acure.DETALLE INTO r_descripcion
FROM 
ACAD_CARGA_UNID_RA_EV acure 
INNER JOIN ACAD_CARGA_UNIDAD_RA accra ON acure.ID_CARGA_UNIDAD_RA =accra.ID_CARGA_UNIDAD_RA 
WHERE accra.ID_CARGA_UNIDAD  = p_id_carga_unidad
AND rownum=1;

    RETURN r_descripcion;
END;

