FUNCTION ft_fecha_cambio_escuela(p_id_persona INTEGER,p_id_plan_programa INTEGER)
RETURN date IS 

p_fecha date;

begin

  select fecha_registro into p_fecha
  from (
        select fecha_registro from Acad_Alumno_Contrato where id_persona=p_id_persona and id_plan_programa<>p_id_plan_programa
        order by Fecha_Registro)
  where rownum=1;
  
  return p_fecha;

end;

