SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_fecha_record_old(

  p_id_curso_detalle in integer,

  p_id_plan_curso IN integer,

  p_id_plan IN integer,

  p_id_tipo_condicion IN  integer,

  p_id_persona IN  integer, 

  p_promedio in NUMBER

) RETURN date is



  v_fecha DATE;


BEGIN

	SELECT fecha_registro INTO v_fecha  FROM (

	/*era solo apc.fecha_registro*/

	SELECT nvl(accd.fecha_fin,apc.fecha_registro) AS fecha_registro FROM (

	

	       SELECT apc.id_curso_detalle,aca.ID_PLAN_CURSO,aca.promedio,

                        ACA.FECHA_REGISTRO,ACA.ID_CARGA_CURSO,aca.id_tipo_condicion

                    from vw_acad_curso_plan apc 

                    inner join VW_CURSO_EQUIVALENTE vce on VCE.ID_CURSO_DETALLE=apc.ID_CURSO_DETALLE

                    inner join ACAD_CURSO_DETALLE acd on ACD.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and acd.id_area = apc.id_parent 

                    inner join acad_curso_alumno aca on ACA.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and aca.id_persona=p_id_persona and aca.estado='1'

                    

                    where apc.id_plan=p_id_plan and apc.ID_PLAN_CURSO_ELECTIVO is null

                    and aca.id_tipo_condicion NOT in (7)

                    union

                    select apc.id_curso_detalle,aca.ID_PLAN_CURSO,aca.promedio,ACA.FECHA_REGISTRO,ACA.ID_CARGA_CURSO,aca.id_tipo_condicion

                    from VW_ACAD_CURSO_PLAN apce inner join acad_plan_curso apc on APC.ID_PLAN_CURSO=APCE.ID_PLAN_CURSO_ELECTIVO

                    inner join VW_CURSO_EQUIVALENTE vce on VCE.ID_CURSO_DETALLE=apce.ID_CURSO_DETALLE

                    inner join ACAD_CURSO_DETALLE acd on ACD.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and acd.id_area = apce.id_parent 

                    inner join acad_curso_alumno aca on ACA.ID_CURSO_DETALLE=VCE.CURSO_EQUIV and aca.id_persona=p_id_persona and aca.estado='1'

                    where apce.id_plan=p_id_plan and apce.ID_PLAN_CURSO_ELECTIVO is not null

                    and aca.id_tipo_condicion not in (7)                    

                   ) apc 

                   /*recien agregado 

                    */
                   left join (select accd.id_carga_curso,max(amd.fecha_fin) fecha_fin

                   from acad_carga_curso_docente accd inner join acad_modulo_detalle amd on amd.id_modulo_detalle=accd.id_modulo_detalle

                  group by accd.id_carga_curso) accd on accd.id_carga_curso=apc.id_carga_curso
                   /*termina

                   */
                   

              WHERE  apc.ID_CURSO_DETALLE = p_id_curso_detalle

             -- AND apc.id_plan_curso = p_id_plan_curso
              AND apc.id_tipo_condicion = p_id_tipo_condicion

              AND nvl(accd.fecha_fin,apc.fecha_registro) IS NOT NULL 

              ORDER BY apc.FECHA_REGISTRO asc

              )

              WHERE fecha_registro IS NOT NULL AND rownum=1

             ;

      return v_fecha;

end;

