SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_filial_alumno(p_id_persona in integer) 
return varchar2
is
  p_Sede varchar2(500);
begin
    select Distinct Sede into p_Sede
    from (select Aap.Sede from Vw_Alumno_Plan_Programa aap
          where aap.id_persona=p_id_persona and aap.estado=1
          order by Aap.Id_Nivel_Ensenanza,Aap.Nombre_Plan desc)
    where rownum=1;
    return p_Sede;
end;

