\set ON_ERROR_STOP ON

function ft_formato_horario(v_id_carga_curso in integer)
RETURN varchar2 IS
	p_formato	  varchar2(2000);
		   
BEGIN

	select Tfm.Id_Tipo_Formato_Modulo into p_formato
  from acad_carga_curso acc inner join acad_semestre_programa asp on Asp.Id_Semestre_Programa=Acc.Id_Semestre_Programa
  inner join Acad_Modulo_Detalle amd on Amd.Id_Semestre_Programa=Asp.Id_Semestre_Programa
  inner join Acad_Modulo am on Am.Id_Modulo=Amd.Id_Modulo
  inner join Tipo_Formato_Modulo tfm on Tfm.Id_Tipo_Formato_Modulo=Am.Id_Tipo_Formato_Modulo
  where Acc.Id_Carga_Curso=89782;
     
return p_formato;

END;

