SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_horario_teoria(v_id_carga_curso in integer)
RETURN varchar2 IS
	p_horario	  varchar2(2000);
		   
BEGIN

	select horario into p_horario
  from (select Accd.horario 
        from Acad_Carga_Curso_Docente accd inner join Tipo_Horario th on Accd.Id_Tipo_Horario=Th.Id_Tipo_Horario and Th.Codigo like 'T%'
        and accd.Id_Carga_Curso=v_id_carga_curso
        order by Th.Codigo) 
  where rownum=1;
     
return p_horario;

END;

