function ft_ley_30220(p_id_persona in integer) 
return varchar2
is
  p_resultado varchar2(500);
begin
  select Distinct 'SI' into p_resultado
  from vw_acad_carga_academica aca inner join acad_carga_curso_docente accd on accd.id_carga_curso=Aca.Id_Carga_Curso
  where Aca.Id_Curso_Modo=1 and substr(Aca.Semestre,0,4)<2014 and Accd.Id_Tipo_Docente=1 and Accd.Id_Persona=p_id_persona;
  return p_resultado;
end;

