SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

function ft_licencia_sunedu(p_id_persona in integer) 
return varchar2
is
  p_resultado varchar2(500);
begin
  select (case when anio>2013 then 'SI' else 'NO' end) into p_resultado
  from (select min(substr(Aca.Semestre,0,4)) anio
        from vw_acad_carga_academica aca inner join acad_carga_curso_docente accd on accd.id_carga_curso=Aca.Id_Carga_Curso
        where Accd.Id_Persona=p_id_persona);
  return p_resultado;
end;

