function ft_max_codigo_conv(v_num number,v_convocatoria in varchar2) RETURN VARCHAR2 AS

  p_codigo VARCHAR2(20);

  p_codigo_semestre VARCHAR2(20);
  p_codigo_anio VARCHAR2(20);

BEGIN



      select nvl((select max(codigo) from moises.persona_natural_alumno where /*codigo like substr(to_CHAR(sysdate,'YYYY-MM-DD'),0,4)||'%' OR*/
 codigo LIKE to_char(v_convocatoria)||'%'),v_convocatoria||'0000')+v_num into p_codigo
      from dual;

      return p_codigo;

end;

