function ft_max_codigo(v_num number) RETURN VARCHAR2 AS

  p_codigo VARCHAR2(20);

  p_codigo_semestre VARCHAR2(20);
  p_codigo_anio VARCHAR2(20);

BEGIN



      select max(replace(semestre,'-','')) into p_codigo_semestre
      ---replace(substr(semestre,0,6),'-','')||'0000' into p_codigo_semestre
      from acad_semestre where estado_admision=1;

     
        select replace(substr(semestre,0,5),'-','') into p_codigo_anio

      from acad_semestre where estado_admision=1;



      select nvl((select max(codigo) from moises.persona_natural_alumno where /*codigo like substr(to_CHAR(sysdate,'YYYY-MM-DD'),0,4)||'%' OR*/
 codigo LIKE to_char(p_codigo_semestre)||'%'),p_codigo_semestre||'0000')+v_num into p_codigo
      from dual;

      return p_codigo;

end;

