function ft_max_id_sem_curso(p_id_persona in integer,p_id_plan_programa in integer) 

return number

is

  p_id_semestre number;

begin  

  select id_semestre into p_id_semestre
  from acad_semestre where semestre=(select max(Semestre) from Vw_Acad_Curso_Alumno where id_persona=p_id_persona and Id_Plan_Programa=p_id_plan_programa and Id_Tipo_Condicion in (1,2));
  
  return p_id_semestre;

end;

