FUNCTION FT_MAX_SEMESTRE_ALUMNO_EAP
(
  V_ID_PERSONA IN NUMBER 
, V_ID_ESCUELA IN NUMBER 
) RETURN VARCHAR2 AS 
p_semestre varchar2(500);
BEGIN

  select max(Semestre) into p_semestre from Vw_Acad_Alumno_Contrato 
  where id_persona=V_ID_PERSONA and id_escuela=V_ID_ESCUELA and estado='1' --and Id_Resp_Financiero is not null
  ;
  
  RETURN p_semestre;

END;

