SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON

FUNCTION FT_MAX_SEMESTRE_ALUMNO_T
(
  V_ID_PERSONA IN NUMBER 
) RETURN VARCHAR2 AS 
p_semestre varchar2(500);
BEGIN
  SELECT MAX(ASM.SEMESTRE) SEMESTRE INTO p_semestre
  FROM ACAD_ALUMNO_CONTRATO AAC INNER JOIN ACAD_SEMESTRE_PROGRAMA ASP ON ASP.ID_SEMESTRE_PROGRAMA=AAC.ID_SEMESTRE_PROGRAMA 
                              AND AAC.ID_PERSONA=V_ID_PERSONA
                              AND AAC.ESTADO='1'
  INNER JOIN ACAD_SEMESTRE ASM ON ASM.ID_SEMESTRE=ASP.ID_SEMESTRE WHERE SUBSTR(ASM.SEMESTRE,-2) IN ('-1','-2');
  
  RETURN p_semestre;

END;

